import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('customer'); // Default user type

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    let loginUrl = '';

    // Determine the login URL based on user type
    switch (userType) {
      case 'admin':
        loginUrl = 'http://localhost:8080/admin/login';
        break;
      case 'stylist':
        loginUrl = 'http://localhost:8080/stylists/login';
        break;
      default:
        loginUrl = 'http://localhost:8080/customers/login';
    }

    // Make POST request to backend
    axios.post(loginUrl, {
      email: email,
      password: password
    }, {
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      // Handle successful login response
      console.log('Login successful:', response.data);

      // Reset the form fields
      setEmail('');
      setPassword('');

      // Redirect based on user type
      switch (userType) {
        case 'admin':
          navigate('/admin');  // Redirect to the admin dashboard
          break;
        case 'stylist':
          navigate('/stylists');  // Redirect to the stylist dashboard
          break;
        default:
          navigate('/services');  // Redirect to the customer services page
      }
    })
    .catch(error => {
      // Handle login error
      console.error('Login failed:', error.response?.data || error.message);
      alert('Login failed! Please check your credentials and try again.');
    });
  };

  const handleRegister = () => {
    if (userType === 'admin') {
      alert('Admin registration is not allowed.');
      return;
    }
    navigate('/register', { state: { userType } });
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLoginSubmit}>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>User Type:</label>
          <select value={userType} onChange={(e) => setUserType(e.target.value)} required>
            <option value="customer">Customer</option>
            <option value="stylist">Stylist</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button type="submit">Submit</button>
        <button type="button" onClick={handleRegister} className="register-button">Register</button>
      </form>
    </div>
  );
};

export default Login;
